function ajusteMobile(){var a=0;jQuery("ul.products .woocommerce-loop-product__title").each(function(){height_div=jQuery(this).height();if(height_div>a){a=height_div}});jQuery("ul.products .woocommerce-loop-product__title").each(function(){jQuery(this).css("min-height",a+20)})}jQuery(document).ready(function(){ajusteMobile()});jQuery(window).resize(function(){ajusteMobile()});